import { createStore } from 'vuex'
import * as api from '@/utils/api.js'

export default createStore({
  state: {
    search: '',
    ERR: false,
    RUL: false,
    MA: false,
    msgType: 'ERR',
    msgTypeTemp: 'ERR',
    msgTypeStatus: false,
    msgLang: 'zh-TW',
    msgUseId: 0,
    msgDelId: 0,
    msgDelIdTemp: 0,
    msgDelIdAPI: [],
    msgAdd: {
      name: '',
      title: {
        'zh-TW': ``,
        'zh-CN': ``,
        'en': ``
      },
      content: {
        'zh-TW': ``,
        'zh-CN': ``,
        'en': ``
      },
      contentTop: {
        'zh-TW': ``,
        'zh-CN': ``,
        'en': ``
      },
      contentBottom: {
        'zh-TW': ``,
        'zh-CN': ``,
        'en': ``
      },
      contentHtml: ``,  
      sendTypeList: "[\"MAIL\"]",
      sendTypeListTemp: "[\"MAIL\"]"
    },
    msgChanged: false,
    msgChangedTemp: false,
    msgChangedSave: false,
    delModal: false,
    addModal: false,
    editStatus: false,
    saveStatus: false,
    cancelcheck: false,
    toolbtn: {
      edit: true,
      delete: true,
      save: false,
      cancel: false,
    },
    data: []
  },
  getters: {
    sampleMsg(state){
      if(state.search){
        return state[state.msgType].filter(item => item.id === state[state.msgType][0].id ).filter( msg => {
          return msg['name'].match(state.search)
        })
      }
      return state[state.msgType] 
    },
    newMsg(state){
      if( state[state.msgType] !== false ){
        if(state.search){
          if(state.msgDelId !== 0 || state.msgChanged !==  false){ 
            if (state.msgChanged !==  false){                  // 如果已經有元素被刪除時，讀取刪除後的內容
              state.msgChanged = [...state.msgChanged.filter( msg =>{ return msg['id'] != state.msgDelId })]
              return  state.msgChanged.slice(1).filter( msg => {
                return msg['name'].match(state.search)
              })  
            }
            state.msgChanged = [...state[state.msgType].filter( msg =>{ return msg['id'] != state.msgDelId })]
            return  state.msgChanged.slice(1).filter( msg => {
              return msg['name'].match(state.search)
            })                    
          }
          return state[state.msgType].slice(1).filter( msg => {
            return msg['name'].match(state.search)
          })
        }
        if(state.msgDelId !== 0 || state.msgChanged !==  false){
          if (state.msgChanged !==  false){     // 如果已經有元素被刪除或變更時，讀取刪除後的內容
            state.msgChanged = [...state.msgChanged.filter( msg =>{ return msg['id'] != state.msgDelId })]
            return state.msgChanged.slice(1)    // 保留 state.msgChanged 中 msg.msgId 為 0 的內容以便進入判斷式，但回傳時用 slice 篩選掉 msg.msgId 為 0 的範本
          }
          state.msgChanged = [...state[state.msgType].filter( msg =>{ return msg['id'] != state.msgDelId })]
          return  state.msgChanged.slice(1)    
        }
        return state[state.msgType].slice(1)
      }
    },
    showMsg(state) {
      if(state[state.msgType] !== false){
        return state[state.msgType].find(msg => msg['id'] > state.msgUseId - 1 )
      } 
    }
  },
  mutations: {
    resetDeletMsgToolBtn(state){
      state.msgDelId = 0
      if(state.msgTypeStatus){        // 辨識此次 cancel modal 之觸發是由 msgType 改變造成的，則重置左側選單之暫存。
        state.msgChanged = false
        state.msgTypeStatus = false  
      }     
    },
    setSearchStatus(state, input) { 
      state.search = input
    },
    setMsgType(state, type){
      if(state.toolbtn.edit === false ){ // 當有修改狀態下想要切換選單，不能直接切換要詢問確認因此先把切換值暫存
        if(type === 'ERR'){
          state.msgTypeTemp = 'ERR'
        } else if(type === 'RUL'){
          state.msgTypeTemp = 'RUL'
        } else if(type === 'MA'){
          state.msgTypeTemp = 'MA'
        }
        state.msgTypeStatus = true    // 辨識此次 cancel modal 之觸發是由 msgType 改變造成的。
        state.cancelcheck = true     // 觸發 cancel modal ，但要整個執行完才觸發！！！！
      } else {
        if(type === 'ERR'){
          state.msgType = 'ERR'
        } else if(type === 'RUL'){
          state.msgType = 'RUL'
        } else if(type === 'MA'){
          state.msgType = 'MA'
        }
        this.commit('setToolBtn', 'save')   // 重置上方功能按鈕
      }
      state.msgUseId = state[state.msgType][0].id    // 切換類別時重置選取為範本
      state.msgLang = 'zh-TW'                                 // 切換類別時重置語言為繁體中文
      state.msgChanged = false
    },
    cancelConfirm(state, answer){       // cancel modal 被觸發
      if(answer === 'yes'){
        state.msgType = state.msgTypeTemp         // 此時才該讓 modal 暫存的 msgTypeTemp 啟動
        state.msgTypeTemp = state.msgType        // 同步
        state.msgUseId = state[state.msgType][0]['id']
        if(state.msgChangedSave){
          state.msgChanged = state.msgChangedSave?.map(n=>n)
        }
        state.editStatus = false
        this.commit('resetDeletMsgToolBtn')     // 確定不儲存資料，觸發 resetDeletMsgToolBtn 將暫存 msgDelId  state.msgChanged 清空
        this.commit('setToolBtn', 'save')   // 重置上方功能按鈕
      } else if(answer === 'no'){
        state.cancelcheck = false
      }
    },
    setMsgLang(state, lang){
      if(lang === 'zh-TW'){
        state.msgLang = 'zh-TW'
      } else if(lang === 'zh-CN'){
        state.msgLang = 'zh-CN'
      } else if(lang === 'en'){
        state.msgLang = 'en'
      }
    },
    setMsgId(state, id){
      state.msgUseId = id     
      state.msgLang = 'zh-TW' // 重置語言
    },
    setToolBtn(state, click){
      if(click === 'edit'){
        state.cancelcheck = false
        state.toolbtn.edit = false     
        state.toolbtn.delete = false
        state.toolbtn.cancel = true
        state.toolbtn.save = true 
        state.editStatus = true
      } else if(click === 'delete'){
        state.cancelcheck = false
        state.toolbtn.edit = false
        state.toolbtn.delete = false
        state.toolbtn.save = true 
        state.toolbtn.cancel = true
      } else if(click === 'save'){      // 此處之 save 不會儲存資料，單純改樣式跟 state，儲存資料的 mutation 分開到 saveMsg 處理
        state.toolbtn.edit = true
        state.toolbtn.delete = true
        state.toolbtn.save = false
        state.toolbtn.cancel = false
        state.editStatus = false
      } else if(click === 'cancel'){
        state.cancelcheck = true       // 觸發 cancel modal
      }
    },
    setSendTypeList(state){
      if(state.msgAdd.sendTypeList === "[]"){
        state.msgAdd.sendTypeList = "[\"MAIL\"]"
      } else if (state.msgAdd.sendTypeList === "[\"MAIL\"]"){
        state.msgAdd.sendTypeList = "[]"
      }
    },
    deletMsg(state, deleteId){
      state.delModal = true           // 打開面板
      state.msgDelIdTemp = deleteId   // 先存到暫存，待會浮窗中確認時才取出，否則清除
    },
    deleteConfirm(state, answer){
      if(answer === 'yes'){
        state.msgDelId = state.msgDelIdTemp
        state.msgDelIdAPI.push( state[state.msgType].find(msg => msg['id'] >  state.msgDelId - 1 ).notifyMsgId )
        state.delModal = false
      } else if(answer === 'no'){
        state.delModal = false
      }
    },
    openAddModal(state){
      state.addModal = true
    },
    setSaveStatus(state){
      state.saveStatus = true
    },
    addMsgSaveBody(state, msg){ 
      state.msgAdd.content[state.msgLang] = msg.content.value || state.msgAdd.content[state.msgLang]
      state.msgAdd.contentTop[state.msgLang] = `<div class="text-area" style="margin-bottom: 0.5rem"><div class="msg-body" style="margin:0 0 1rem 0"><div class="uneditable" style="margin:0 0 3rem 0">${msg.contentTop.value || ' '}</div><span class="click2edit" style="">`
      state.msgAdd.contentBottom[state.msgLang] = `</span></div><div class="msg-footer">${msg.contentBottom.value || ' '}</div></div>`
      state.msgAdd.title[state.msgLang] = msg.title
    },  
    addMsgSaveName(state, msg){
      state.msgAdd.name = msg.name
    },
    addConfirm(state, answer){
      if(answer == 'yes'){
        if (state.msgChanged ==  false){  // 如果已經沒有元素被刪除時，讀取原本
          state.msgChanged = [...state[state.msgType]]
        }

        if(state.saveStatus){
          // eslint-disable-next-line no-unused-vars
          const {  gmtCreate, gmtModified, id, ...msgChangedT} 
          = state.msgChanged.find(msg => msg['id'] > state.msgUseId - 1 ) 
          // eslint-disable-next-line no-unused-vars
          msgChangedT.i18nList = msgChangedT.i18nList.map(({gmtCreate, gmtModified, id, ...keepAttrs}) => keepAttrs)
          state.msgChangedTemp = msgChangedT
        } else {
          state.msgChangedTemp = state.msgChanged.find(msg => msg['id'] > state.msgUseId - 1 ) 
        }

        state.msgChangedTemp.name = state.msgAdd.name

        state.msgChangedTemp.i18nList[0]['content'] = `${state.msgAdd.contentTop['zh-TW'] + state.msgAdd.content['zh-TW'] + state.msgAdd.contentBottom['zh-TW']}` || state[state.msgType].filter( item => item.id === state.msgUseId )[0]?.i18nList[0]['content']

        state.msgChangedTemp.i18nList[1]['content'] = `${state.msgAdd.contentTop['zh-CN'] + state.msgAdd.content['zh-CN'] + state.msgAdd.contentBottom['zh-CN']}` || state[state.msgType].filter( item => item.id === state.msgUseId )[0]?.i18nList[1]['content']

        state.msgChangedTemp.i18nList[2]['content'] = `${state.msgAdd.contentTop['en'] + state.msgAdd.content['en'] + state.msgAdd.contentBottom['en']}` || state[state.msgType].filter( item => item.id === state.msgUseId )[0]?.i18nList[2]['content']

        state.msgChangedTemp.i18nList[0]['title'] = state.msgAdd.title['zh-TW'] ||  state[state.msgType].filter( item => item.id === state.msgUseId )[0]?.i18nList[0]['title']
        state.msgChangedTemp.i18nList[1]['title'] = state.msgAdd.title['zh-CN'] ||  state[state.msgType].filter( item => item.id === state.msgUseId )[0]?.i18nList[1]['title']
        state.msgChangedTemp.i18nList[2]['title'] = state.msgAdd.title['en'] ||  state[state.msgType].filter( item => item.id === state.msgUseId )[0]?.i18nList[2]['title']


        state.msgChangedTemp.sendTypeList = state.msgAdd.sendTypeList

        if(!state.saveStatus && state.editStatus){              // 若是要新增，則需要 push 進左側暫存區
          state.msgChanged.push({...state.msgChangedTemp})
        }
        state.msgChangedSave = {...state.msgChangedTemp}        // 要給 API 使用的與暫存分開來存
        state.addModal = false
        this.commit('setToolBtn', 'save')   // 重置上方功能按鈕
      } else if(answer == 'no'){
        state.addModal = false
      }      
    },
    initERR(state, payload){
      state.ERR = payload
      console.log('state.ERR:', state.ERR)
      this.commit('clearMsgAdd')
    },
    initRUL(state, payload){
      state.RUL = payload
      this.commit('clearMsgAdd')
    },
    initMA(state, payload){
      state.MA = payload
      this.commit('clearMsgAdd')
    },
    clearMsgAdd(state){
      let arr = ['title', 'content', 'contentTop', 'contentBottom']
      let arr2 = ['zh-TW', 'zh-CN', 'en']
      arr.forEach(element => arr2.forEach(lang => state.msgAdd[element][lang]= ''  ))
      state.msgAdd.name = ''
      state.msgAdd.contentHtml = ''
      state.msgAdd.sendTypeList = "[\"MAIL\"]"
      state.msgAdd.sendTypeListTemp = "[\"MAIL\"]"
      state.msgChanged = false
    }
  },
  actions: { // 呼叫 api
      async handInitERR({commit}){
        const res = await api.apiGetNotifyMessageByTypeIdERR()
        state.msgUseId = state[state.msgType][0].id
        commit('initERR', res.data.data)     
        return res.data.data
      },
      async handInitRUL({commit}){
        const res = await api.apiGetNotifyMessageByTypeIdRUL()
        commit('initRUL', res.data.data)     
        return res.data.data
      },
      async handInitMA({commit}){
        const res = await api.apiGetNotifyMessageByTypeIdMA()
        commit('initMA', res.data.data)     
        return res.data.data
      },
      async handSave({dispatch, commit, state}){
        if(state.msgDelIdAPI.length){  // 若是陣列中有要被刪除的訊息
          await dispatch('handDel')
        } else if(!state.msgDelIdAPI.length && !state.saveStatus){   // 若是陣列中沒有要被刪除的訊息
          await dispatch('handAdd')
        } else {
          commit('addConfirm', 'yes')
          console.log(state[state.msgType])
          console.log(state.msgUseId)
          console.log(state[state.msgType].filter( item => item.id === state.msgUseId ))
          state.msgChangedSave.notifyMsgId = state[state.msgType].filter( item => item.id === state.msgUseId )[0].notifyMsgId
          state.msgChangedSave.name = state[state.msgType].filter( item => item.id === state.msgUseId )[0].name
          state.msgChangedSave.sendTypeList = JSON.stringify(state[state.msgType].filter( item => item.id === state.msgUseId )[0].sendTypeList)
          await dispatch('handUpdate')          
        }
      },
      async handUpdate({state}){
        await api.apiUpdateNotifyMessage(state.msgChangedSave)
        // window.location.reload()
        console.log('finish update?')     
      }, 
      async handAdd({state, dispatch}){
        await api.apiInsertNotifyMessage(state.msgChangedSave)
        dispatch('handInitERR')
        dispatch('handInitRUL')
        dispatch('handInitMA')
        // window.location.reload()
        console.log('finish add?')     
      }, 
      async handDel({state, dispatch}){
        await api.apiDeleteNotifyMessageBatch({data: state.msgDelIdAPI})    
        state.msgDelIdAPI = [] 
        dispatch('handInitERR')
        dispatch('handInitRUL')
        dispatch('handInitMA')
        console.log('finish del?')    
      }    
  },
  modules: {
  }
})
